mapper.delete:
UPDATE perm
SET valid = FALSE
WHERE id = #{id}

UPDATE perm
SET valid = FALSE, modifier = #{modifier}, modified_time = #{modifiedTime}
WHERE id = #{id}

mapper.update:
UPDATE perm
SET active = TRUE, modifier = #{modifier}, modified_time = #{modifiedTime}
WHERE id = #{id}

UPDATE perm
SET active = FALSE, modifier = #{modifier}, modified_time = #{modifiedTime}
WHERE id = #{id}

mapper.select:
SELECT count(*)
FROM role_perm
WHERE perm_id = #{id}
